import { Outlet, Link, useNavigate } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import { useAuth } from '../contexts/AuthContext';
import { useToast } from '../contexts/ToastContext';
import { useLocation } from '../contexts/LocationContext';
import styles from './MainLayout.module.scss';
import { useState, useEffect, useRef, type FormEvent } from 'react';
import LocationModal from '../components/layout/LocationModal';

// --- SVG icon component for Search ---
const SearchIcon = () => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    viewBox="0 0 24 24"
    className={styles.searchIcon}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <circle cx="11" cy="11" r="8"></circle>
    <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
  </svg>
);

// --- Added Arrow Down Icon ---
const ArrowDownIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="16" height="16" fill="none" stroke="currentColor" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round">
    <polyline points="6 9 12 15 18 9"></polyline>
  </svg>
);

const MainLayout = () => {
  const { itemCount } = useCart();
  const { user, logout } = useAuth();
  const { showToast } = useToast();
  const navigate = useNavigate();
  const { 
    locationName, 
    isModalOpen, 
    openModal, 
    closeModal 
  } = useLocation();
  
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);

  // --- 2. Add state for the search query ---
  const [searchQuery, setSearchQuery] = useState('');

  const handleLogout = () => {
    logout();
    showToast('Logged out successfully', 'success');
    navigate('/auth/login');
  };
  
  const getInitials = (name: string) => {
    if (!name) return 'U';
    const parts = name.split(' ');
    if (parts.length > 1) {
      return (parts[0][0] + parts[parts.length - 1][0]).toUpperCase();
    }
    return name[0].toUpperCase();
  };

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsDropdownOpen(false);
      }
    };
    if (isDropdownOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isDropdownOpen]);

  // --- 3. Handle search form submission ---
  const handleSearchSubmit = (e: FormEvent) => {
    e.preventDefault();
    if (searchQuery.trim().length === 0) {
      return; // Don't search for nothing
    }
    // Navigate to a new search page with the query
    navigate(`/search?q=${searchQuery}`);
    setSearchQuery(''); // Clear the search bar
  };

  return (
    <div>
      <nav className={styles.navbar}>
        
        {/* 1. Left Section */}
        <div className={styles.brandSection}>
          <Link to="/" className={styles.navBrand}>QuickCart</Link>
          <div 
            className={styles.deliveryInfo} 
            onClick={openModal}
            style={{ cursor: 'pointer' }}
          >
            <h4>Delivery in 10 minutes</h4>
            <span>{locationName} <ArrowDownIcon /></span>
          </div>
        </div>

        {/* 2. Middle Section (Updated to a Form) */}
        <form className={styles.searchContainer} onSubmit={handleSearchSubmit}>
          <SearchIcon />
          <input 
            type="text" 
            placeholder='Search "bread"' 
            className={styles.searchInput}
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </form>

        {/* 3. Right Section */}
        <div className={styles.navLinks}>
          {user && (
            <>
              {user.role === 'ADMIN' && (
                 <Link to="/admin" className={styles.navLink}>Admin</Link>
              )}
              
              <div className={styles.profileContainer} ref={dropdownRef}>
                <div 
                  className={styles.profileIcon}
                  onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                >
                  {getInitials(user.name)}
                </div>

                {isDropdownOpen && (
                  <div className={styles.dropdownMenu}>
                    <Link to="/my-orders" className={styles.dropdownItem} onClick={() => setIsDropdownOpen(false)}>My Orders</Link>
                    <Link to="/profile" className={styles.dropdownItem} onClick={() => setIsDropdownOpen(false)}>My Profile</Link>
                    <Link to="/profile/update-password" className={styles.dropdownItem} onClick={() => setIsDropdownOpen(false)}>Update Password</Link>
                    <button onClick={handleLogout} className={styles.dropdownItem}>Logout</button>
                  </div>
                )}
              </div>
            </>
          )}
          {!user && (
            <Link to="/auth/login" className={styles.navLink}>Login</Link>
          )}
          
          <Link to="/cart" className={styles.cartLink}>
            Cart <span className={styles.cartCount}>{itemCount}</span>
          </Link>
        </div>
      </nav>

      <main style={{ padding: '20px 30px' }}>
        <Outlet />
      </main>

      <LocationModal 
        isOpen={isModalOpen} 
        onClose={closeModal} 
      />
    </div>
  );
};

export default MainLayout;